mapper.select:
		
			select no,
       					 name
			from users
			where id=#{id}
			and password=#{password}

mapper.delete:
	
		delete from guestbook
		where no=#{no}
		and password=#{password}

mapper.update:
	
		update users
		set         name=#{name},
		   		       password=#{password},
		    	       gender=#{gender}
		where  no=#{no}
